import React from "react";
import { NavLink } from "react-router-dom";

const Navbar = () => {

  const skills = ["html/css","javascript","react.js","node.js", "express","mongodb","react-native","git","ui/ux basics","python", "rust"];
  const [click, setClick] = React.useState(false);
  const [skillsClicked, setSkillsClicked] = React.useState(false);

  function hamburgerClicked (e) {
    if (click === false) {
        setClick(true)
        // console.log('hamburger pressed')
    } else {
        setClick(false)
        // console.log('hamburger closed')
    }
};

  function skillsNavClicked (e) {
    if (skillsClicked === false) {
      setSkillsClicked(true)
        console.log('skills nav bar pressed')
    } else {
      setSkillsClicked(false)
        console.log('skills nav bar closed')
    }
  };

  return (
    <div className="full-nav">
      <div className="nav-1">
      <a href="/">
        <div className="nav-box"> 
          trang-do-thuy
        </div> 
        </a>
      </div>
    <div className="nav-2">
      <a href="/"><div className="nav-box">
        <NavLink 
          to={"/"}
          style={ ({isActive}) => (
          isActive ? linkStyles.activeLink : linkStyles.defaultLink
        )}>
          _hello
        </NavLink>
      </div></a>

      <a href="/about-me"><div class="nav-box">
        <NavLink
          to="/about-me"
          style={ ({isActive}) => (
              isActive ? linkStyles.activeLink : linkStyles.defaultLink
              )}>
        _about-me
        </NavLink>
      </div></a>

      <a href="/projects"><div class="nav-box">
        <NavLink
          to="/projects"
          style={ ({isActive}) => (
              isActive ? linkStyles.activeLink : linkStyles.defaultLink
              )}>
        _projects
        </NavLink>
      </div></a>
      </div>

      <div class="nav-3">
      <a href="/contact"><div class="nav-box">
          <NavLink
          to="/contact"
          style={ ({isActive}) => (
              isActive ? linkStyles.activeLink : linkStyles.defaultLink
              )}>
          _contact-me
          </NavLink>
        </div></a>
      </div>

      {/* <div className="nav-icon" onClick={handleClick}>
            <i className={click ? "fa fa-times" : "fa fa-bars"}></i>
          </div> */}

      <div className="nav-4">
        <div className="nav-box-1"> 
        {click ? (<button 
        className="side-bar-btn"
        onClick={hamburgerClicked}>x</button>) : (<button 
        className="side-bar-btn"
        onClick={hamburgerClicked}>=</button>)}


        {click && (<div className="hamburgerText">
          {/* <p class="navSeparator"><strong>Bootcamps â†“</strong></p> */}
          <a className="hamTextBox" href="/">_hello</a>
          <a className="hamTextBox" href="/about-me">_about-me</a>
          <button 
          className="side-bar-btn-nav"
          onClick={skillsNavClicked}>_skills</button>
          {skillsClicked && (<div className="skills-nav">
                        {skills.map((skill, i) => {
                            return <div className="skills-nav-extend" key={i}>&gt;  {skill}</div>
                        })}
                </div>)}
          <a className="hamTextBox" href="/projects">_projects</a>
          <a className="hamTextBox" href="/contact">_contact-me</a>
          {/* <a class="HamDD" href="/barcelona-code-school/javascript-full-stack-online-bootcamp/"><strong>JavaScript Full-Stack Online Mentored Bootcamp</strong></a><a class="HamDD" href="/ux-design-bootcamp-in-barcelona-code-school/"><strong>UX/UI Design in-person Bootcamp</strong></a><p class="navSeparator"><strong>In-person short courses â†“</strong></p><a class="HamDD" href="/responsive-web-design-with-css3/">Responsive web design / CSS3</a><a class="HamDD" href="/programming-with-javascript/">Programming with JavaScript</a><a class="HamDD" href="/reactjs-crash-course/">React.js Crash Course</a><a class="HamDD" href="/backend-development-with-node-express-mongodb/">Back-end development with Express</a> */}
          </div>)}


        </div> 
      </div>
    </div>
      );


}

export default Navbar;

const linkStyles = {
  defaultLink: {
    color: "#6272A4",
    textDecoration: "none",
  },
  activeLink: {
    // textDecoration: "pink underline",
    color: "#F8F8F2",
    // color: "#dd82b8",
    textShadow: "1px 3px #dd82b8",
